import * as React from "react";
import Typography from "@mui/material/Typography";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
} from "@mui/material";
import dateFormat from "dateformat";
// import PhotoService from "../services/photoService";
import PhotoData from "../types/photoData";
import axios from "axios";
import { PhotoCard } from "./photoCard";

// every card has:
// image, like button, title, explanation, date

// make another component that takes a start and end date and returns the images in that set (inclusive)

export const PhotoGallery = (): JSX.Element => {
  const [lastDate] = React.useState<Date>(new Date());
  const [isLoading, setLoading] = React.useState<boolean>(false);
  const [photos, setPhotos] = React.useState<PhotoData[]>([]);

  React.useEffect(() => {
    // fetch the first 10 photos
    setLoading(true);
    lastDate.setDate(lastDate.getDate() - 10);
    const formattedDate = dateFormat(lastDate, "yyyy-mm-dd");
    // console.log(formattedDate);
    axios
      .get<PhotoData[]>(
        `https://api.nasa.gov/planetary/apod?api_key=ykzQINaucAEoc1RW6I3CqVke24wjlu9JAyrDVcKd&start_date=${formattedDate}`
      )
      .then((response) => {
        console.log(response.data);
        setLoading(false);
        setPhotos(response.data);
      });
  }, []);

  var mydate = new Date("2014-04-03");

  const renderIsLoading = () => {
    if (isLoading) {
      return <h1>loading...</h1>;
    }
  };

  return (
    <>
      {renderIsLoading()}
      {photos.map((photo) => (
        <PhotoCard
          date={photo.date}
          explanation={photo.explanation}
          title={photo.title}
          url={photo.url}
        />
      ))}
    </>
  );
};

export default PhotoGallery;
