import * as React from "react";
import Typography from "@mui/material/Typography";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
} from "@mui/material";
import dateFormat from "dateformat";

// every card has:
// image, like button, title, explanation, date

// make another component that takes a start and end date and returns the images in that set (inclusive)

type PhotoCardProps = {
  date: string;
  explanation: string;
  title: string;
  url: string;
};

export const PhotoCard = ({
  date,
  explanation,
  title,
  url,
}: PhotoCardProps): JSX.Element => {
  const [isLiked, setLiked] = React.useState<boolean>(false);

  const renderLikeButton = (): JSX.Element => {
    if (isLiked) {
      return (
        <Button
          onClick={() => setLiked(!isLiked)}
          variant="contained"
          size="small"
        >
          unlike
        </Button>
      );
    }

    return (
      <Button
        onClick={() => setLiked(!isLiked)}
        variant="contained"
        size="small"
      >
        Like
      </Button>
    );
  };

  return (
    <Card
      sx={{ maxWidth: 800, margin: "auto", marginTop: "60px", boxShadow: 1 }}
    >
      <CardMedia
        component="img"
        // height="600"
        image={url}
        alt={title}
      />
      <CardContent sx={{ mx: 5 }}>
        <Typography
          sx={{
            marginTop: "30px",
          }}
          gutterBottom
          variant="h2"
          component="div"
        >
          {title}
        </Typography>
        <Typography gutterBottom variant="h3" component="div">
          {dateFormat(date, "dddd, mmmm dS, yyyy")}
        </Typography>
        <Typography sx={{ marginTop: 4 }}>{explanation}</Typography>
      </CardContent>
      <CardActions>{renderLikeButton()}</CardActions>
    </Card>
  );
};
