import * as React from "react";
import Typography from "@mui/material/Typography";
import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
} from "@mui/material";
import dateFormat from "dateformat";
import theme from "../theme";
// @ts-ignore
import Heart from "react-animated-heart";

type PhotoCardProps = {
  copyright?: string;
  date: string;
  explanation: string;
  title: string;
  url: string;
  media_type: string;
};

export const PhotoCard = ({
  copyright,
  date,
  explanation,
  title,
  url,
  media_type,
}: PhotoCardProps): JSX.Element => {
  const [isLiked, setLiked] = React.useState<boolean>(false);

  // Render the like button that every photo contains
  const renderLikeButton = (): JSX.Element => {
    return (
      <>
        <Heart isClick={isLiked} onClick={() => setLiked(!isLiked)} />
      </>
    );
  };

  return (
    // <Box textAlign="center">
    //   <Box
    //     component="img"
    //     sx={{ maxWidth: "800px", maxHeight: "400px" }}
    //     src={url}
    //   ></Box>
    // </Box>
    <Card
      sx={{
        maxWidth: 700,
        margin: "auto",
        marginTop: "100px",
        boxShadow: 0,
        backgroundColor: "#fafafa",
      }}
    >
      <Typography sx={{ marginBottom: 2 }} variant="h3">
        {copyright}
      </Typography>
      <CardMedia
        component={media_type == "video" ? "iframe" : "img"}
        height={media_type == "video" ? "600" : ""}
        src={url}
      />
      <CardContent sx={{ mx: 0 }}>
        <Typography
          sx={{
            marginTop: "2px",
          }}
          gutterBottom
          variant="h2"
        >
          {title}
        </Typography>
        <Typography sx={{ marginTop: 0 }} variant="body2" gutterBottom>
          {dateFormat(date, "mmmm dS, yyyy")}
        </Typography>
        {renderLikeButton()}
        {/* <Typography sx={{ marginTop: 2 }}>{explanation}</Typography> */}
      </CardContent>
      <CardActions></CardActions>
    </Card>
  );
};
